function mobileNav(){$(".mobile-nav-toggle").on("click",function(){$(this).hasClass("is-open")?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")}),$(".menu-link").click(function(){$("#mobile-nav, .mobile-nav-toggle").toggleClass("is-open")})}function smoothScroll(n){$('a[href^="#"]').on("click",function(o){var l=$($(this).attr("href"));if(l.length)return o.preventDefault(),$("html, body").animate({scrollTop:l.offset().top},n),!1})}$(function(){smoothScroll(700),mobileNav()});