function mobileNav(){$(".mobile-nav-toggle").on("click",function(){$(this).hasClass("is-open")?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")}),$(".menu-link").click(function(){$("#mobile-nav, .mobile-nav-toggle").toggleClass("is-open")})}function smoothScroll(e){$('a[href^="#"]').on("click",function(o){var n=$($(this).attr("href"));if(n.length)return o.preventDefault(),$("html, body").animate({scrollTop:n.offset().top},e),!1})}function projects(){$(".container-project").hover(function(){$(this).find(".image-text").hide(100)},function(){$(this).find(".image-text").show("slow")})}$(function(){smoothScroll(700),mobileNav(),projects()});