function mobileNav(){$(".mobile-nav-toggle").on("click",function(){var o=$(this).hasClass("is-open");o?$(".mobile-nav-toggle, .mobile-nav").removeClass("is-open"):$(".mobile-nav-toggle, .mobile-nav").addClass("is-open")})}function smoothScroll(o){$('a[href^="#"]').on("click",function(l){var e=$($(this).attr("href"));e.length&&(l.preventDefault(),$("html, body").animate({scrollTop:e.offset().top},o))})}$(function(){smoothScroll(300),mobileNav()});